<?phpdefined( 'K_PATH' ) or die( 'DIRECT ACCESS IS NOT ALLOWED' );class Admin_Controller_Gui_Form extends Admin_Controller_Gui {    public function __construct($nodeData)    {		parent::__construct($this->_options);				$this->nodeData = $nodeData;    }		protected function newGUI()	{		return false;	}    protected function formGUI()	{		$this->tabs['form'] = 'Форма';	        return <<< HTML        <script type="text/javascript">					$('#my-form-builder').formbuilder({				'save_url': '/admin/form/save/key/{$this->nodeData['tree_id']}',				'load_url': '/admin/form/load/key/{$this->nodeData['tree_id']}',				'preview_url': '/admin/form/preview/key/{$this->nodeData['tree_id']}',				'useJson' : true 			});						$('#my-form-builder ul').sortable();			        </script>		<div style="padding: 19px;" class="b-node_information">							<div>				[ <b>{$this->nodeData['tree_link']}</b> ]			</div>						</div>		        <div id="my-form-builder"></div>HTML;    }		protected function editGUI()	{		return false;	}}